const app_mcdh47a1d1jl_canvas = document.getElementById('app_mcdh47a1d1jl_gameCanvas');
const app_mcdh47a1d1jl_ctx = app_mcdh47a1d1jl_canvas.getContext('2d');
const app_mcdh47a1d1jl_startButton = document.getElementById('app_mcdh47a1d1jl_startButton');
const app_mcdh47a1d1jl_restartButton = document.getElementById('app_mcdh47a1d1jl_restartButton');
const app_mcdh47a1d1jl_startScreen = document.getElementById('app_mcdh47a1d1jl_startScreen');
const app_mcdh47a1d1jl_gameOverScreen = document.getElementById('app_mcdh47a1d1jl_gameOverScreen');
const app_mcdh47a1d1jl_scoreDisplay = document.getElementById('app_mcdh47a1d1jl_scoreDisplay');
const app_mcdh47a1d1jl_livesDisplay = document.getElementById('app_mcdh47a1d1jl_livesDisplay');
const app_mcdh47a1d1jl_finalScoreDisplay = document.getElementById('app_mcdh47a1d1jl_finalScore');

let app_mcdh47a1d1jl_ship;
let app_mcdh47a1d1jl_asteroids;
let app_mcdh47a1d1jl_bullets;
let app_mcdh47a1d1jl_score;
let app_mcdh47a1d1jl_lives;
let app_mcdh47a1d1jl_gameOver;
let app_mcdh47a1d1jl_gameLoopId;

const app_mcdh47a1d1jl_SCREEN_WIDTH = window.innerWidth;
const app_mcdh47a1d1jl_SCREEN_HEIGHT = window.innerHeight;

app_mcdh47a1d1jl_canvas.width = app_mcdh47a1d1jl_SCREEN_WIDTH;
app_mcdh47a1d1jl_canvas.height = app_mcdh47a1d1jl_SCREEN_HEIGHT;

function app_mcdh47a1d1jl_dist(p1, p2) {
    return Math.sqrt(Math.pow(p1.x - p2.x, 2) + Math.pow(p1.y - p2.y, 2));
}

class app_mcdh47a1d1jl_Ship {
    constructor(x, y) {
        this.x = x;
        this.y = y;
        this.rotation = 0;
        this.thrust = 0;
        this.velocity = { x: 0, y: 0 };
        this.sprite = [
            { x: 0, y: -15 },
            { x: 10, y: 10 },
            { x: -10, y: 10 }
        ];
        this.width = 20;
        this.height = 30;
        this.thrusting = false;
    }

    rotate(dir) {
        this.rotation += dir * 0.1;
    }

    setThrust(thrusting) {
        this.thrusting = thrusting;
    }

    update() {
        if (this.thrusting) {
            this.velocity.x += Math.sin(this.rotation) * 0.1;
            this.velocity.y -= Math.cos(this.rotation) * 0.1;
        }

        this.x += this.velocity.x;
        this.y += this.velocity.y;

        this.velocity.x *= 0.99;
        this.velocity.y *= 0.99;

        if (this.x > app_mcdh47a1d1jl_SCREEN_WIDTH + this.width) this.x = -this.width;
        if (this.x < -this.width) this.x = app_mcdh47a1d1jl_SCREEN_WIDTH + this.width;
        if (this.y > app_mcdh47a1d1jl_SCREEN_HEIGHT + this.height) this.y = -this.height;
        if (this.y < -this.height) this.y = app_mcdh47a1d1jl_SCREEN_HEIGHT + this.height;
    }

    draw() {
        app_mcdh47a1d1jl_ctx.save();
        app_mcdh47a1d1jl_ctx.translate(this.x, this.y);
        app_mcdh47a1d1jl_ctx.rotate(this.rotation);

        app_mcdh47a1d1jl_ctx.strokeStyle = '#fff';
        app_mcdh47a1d1jl_ctx.lineWidth = 2;
        app_mcdh47a1d1jl_ctx.beginPath();
        for (let i = 0; i < this.sprite.length; i++) {
            const point = this.sprite[i];
            if (i === 0) {
                app_mcdh47a1d1jl_ctx.moveTo(point.x, point.y);
            } else {
                app_mcdh47a1d1jl_ctx.lineTo(point.x, point.y);
            }
        }
        app_mcdh47a1d1jl_ctx.closePath();
        app_mcdh47a1d1jl_ctx.stroke();

        if (this.thrusting) {
            app_mcdh47a1d1jl_ctx.beginPath();
            app_mcdh47a1d1jl_ctx.moveTo(this.sprite[1].x, this.sprite[1].y);
            app_mcdh47a1d1jl_ctx.lineTo(this.sprite[2].x, this.sprite[2].y);
            app_mcdh47a1d1jl_ctx.lineTo(this.sprite[1].x + (Math.random() - 0.5) * 10, this.sprite[1].y + 10);
            app_mcdh47a1d1jl_ctx.lineTo(this.sprite[2].x + (Math.random() - 0.5) * 10, this.sprite[2].y + 10);
            app_mcdh47a1d1jl_ctx.stroke();
        }

        app_mcdh47a1d1jl_ctx.restore();
    }
}

class app_mcdh47a1d1jl_Asteroid {
    constructor(x, y, size) {
        this.x = x;
        this.y = y;
        this.size = size;
        this.sides = Math.floor(Math.random() * 5) + 5;
        this.vertices = [];
        this.rotation = Math.random() * Math.PI * 2;
        this.rotationSpeed = (Math.random() - 0.5) * 0.05;
        this.velocity = {
            x: (Math.random() - 0.5) * 2,
            y: (Math.random() - 0.5) * 2
        };
        this.points = [];

        const angleIncrement = Math.PI * 2 / this.sides;
        for (let i = 0; i < this.sides; i++) {
            const angle = i * angleIncrement;
            const radius = size + (Math.random() - 0.5) * size * 0.3;
            this.vertices.push({
                x: Math.cos(angle) * radius,
                y: Math.sin(angle) * radius
            });
        }
    }

    update() {
        this.x += this.velocity.x;
        this.y += this.velocity.y;
        this.rotation += this.rotationSpeed;

        if (this.x > app_mcdh47a1d1jl_SCREEN_WIDTH + this.size) this.x = -this.size;
        if (this.x < -this.size) this.x = app_mcdh47a1d1jl_SCREEN_WIDTH + this.size;
        if (this.y > app_mcdh47a1d1jl_SCREEN_HEIGHT + this.size) this.y = -this.size;
        if (this.y < -this.size) this.y = app_mcdh47a1d1jl_SCREEN_HEIGHT + this.size;
    }

    draw() {
        app_mcdh47a1d1jl_ctx.save();
        app_mcdh47a1d1jl_ctx.translate(this.x, this.y);
        app_mcdh47a1d1jl_ctx.rotate(this.rotation);
        app_mcdh47a1d1jl_ctx.strokeStyle = '#fff';
        app_mcdh47a1d1jl_ctx.lineWidth = 2;
        app_mcdh47a1d1jl_ctx.beginPath();
        for (let i = 0; i < this.vertices.length; i++) {
            const vertex = this.vertices[i];
            if (i === 0) {
                app_mcdh47a1d1jl_ctx.moveTo(vertex.x, vertex.y);
            } else {
                app_mcdh47a1d1jl_ctx.lineTo(vertex.x, vertex.y);
            }
        }
        app_mcdh47a1d1jl_ctx.closePath();
        app_mcdh47a1d1jl_ctx.stroke();
        app_mcdh47a1d1jl_ctx.restore();
    }
}

class app_mcdh47a1d1jl_Bullet {
    constructor(x, y, rotation) {
        this.x = x;
        this.y = y;
        this.rotation = rotation;
        this.speed = 5;
        this.radius = 3;
        this.lifespan = 50;
        this.velocity = {
            x: Math.sin(this.rotation) * this.speed,
            y: -Math.cos(this.rotation) * this.speed
        };
    }

    update() {
        this.x += this.velocity.x;
        this.y += this.velocity.y;
        this.lifespan--;
    }

    draw() {
        app_mcdh47a1d1jl_ctx.fillStyle = '#fff';
        app_mcdh47a1d1jl_ctx.beginPath();
        app_mcdh47a1d1jl_ctx.arc(this.x, this.y, this.radius, 0, Math.PI * 2);
        app_mcdh47a1d1jl_ctx.fill();
    }
}

function app_mcdh47a1d1jl_resetGame() {
    app_mcdh47a1d1jl_ship = new app_mcdh47a1d1jl_Ship(app_mcdh47a1d1jl_SCREEN_WIDTH / 2, app_mcdh47a1d1jl_SCREEN_HEIGHT / 2);
    app_mcdh47a1d1jl_asteroids = [];
    app_mcdh47a1d1jl_bullets = [];
    app_mcdh47a1d1jl_score = 0;
    app_mcdh47a1d1jl_lives = 3;
    app_mcdh47a1d1jl_gameOver = false;
    app_mcdh47a1d1jl_scoreDisplay.textContent = app_mcdh47a1d1jl_score;
    app_mcdh47a1d1jl_livesDisplay.textContent = app_mcdh47a1d1jl_lives;
    app_mcdh47a1d1jl_gameOverScreen.style.display = 'none';
    app_mcdh47a1d1jl_startScreen.style.display = 'flex';
}

function app_mcdh47a1d1jl_createAsteroids(num, size, x, y) {
    for (let i = 0; i < num; i++) {
        let newAsteroid;
        do {
            newAsteroid = new app_mcdh47a1d1jl_Asteroid(
                Math.random() * app_mcdh47a1d1jl_SCREEN_WIDTH,
                Math.random() * app_mcdh47a1d1jl_SCREEN_HEIGHT,
                size
            );
        } while (app_mcdh47a1d1jl_dist({ x: x, y: y }, newAsteroid) < 100);
        app_mcdh47a1d1jl_asteroids.push(newAsteroid);
    }
}

function app_mcdh47a1d1jl_startGame() {
    app_mcdh47a1d1jl_resetGame();
    app_mcdh47a1d1jl_createAsteroids(7, 30, app_mcdh47a1d1jl_ship.x, app_mcdh47a1d1jl_ship.y);
    app_mcdh47a1d1jl_startScreen.style.display = 'none';
    app_mcdh47a1d1jl_gameLoopId = setInterval(app_mcdh47a1d1jl_gameLoop, 1000 / 60);
}

function app_mcdh47a1d1jl_gameLoop() {
    if (app_mcdh47a1d1jl_gameOver) return;

    app_mcdh47a1d1jl_ctx.clearRect(0, 0, app_mcdh47a1d1jl_SCREEN_WIDTH, app_mcdh47a1d1jl_SCREEN_HEIGHT);
    app_mcdh47a1d1jl_ctx.fillStyle = '#000';
    app_mcdh47a1d1jl_ctx.fillRect(0, 0, app_mcdh47a1d1jl_SCREEN_WIDTH, app_mcdh47a1d1jl_SCREEN_HEIGHT);

    app_mcdh47a1d1jl_ship.update();
    app_mcdh47a1d1jl_ship.draw();

    for (let i = app_mcdh47a1d1jl_asteroids.length - 1; i >= 0; i--) {
        const asteroid = app_mcdh47a1d1jl_asteroids[i];
        asteroid.update();
        asteroid.draw();

        if (app_mcdh47a1d1jl_dist(app_mcdh47a1d1jl_ship, asteroid) < asteroid.size) {
            app_mcdh47a1d1jl_lives--;
            app_mcdh47a1d1jl_livesDisplay.textContent = app_mcdh47a1d1jl_lives;
            if (app_mcdh47a1d1jl_lives <= 0) {
                app_mcdh47a1d1jl_endGame();
            } else {
                app_mcdh47a1d1jl_ship = new app_mcdh47a1d1jl_Ship(app_mcdh47a1d1jl_SCREEN_WIDTH / 2, app_mcdh47a1d1jl_SCREEN_HEIGHT / 2);
                app_mcdh47a1d1jl_asteroids.splice(i, 1);
                app_mcdh47a1d1jl_createAsteroids(1, asteroid.size, app_mcdh47a1d1jl_ship.x, app_mcdh47a1d1jl_ship.y);
            }
        }
    }

    for (let i = app_mcdh47a1d1jl_bullets.length - 1; i >= 0; i--) {
        const bullet = app_mcdh47a1d1jl_bullets[i];
        bullet.update();

        if (bullet.lifespan <= 0 ||
            bullet.x < 0 || bullet.x > app_mcdh47a1d1jl_SCREEN_WIDTH ||
            bullet.y < 0 || bullet.y > app_mcdh47a1d1jl_SCREEN_HEIGHT) {
            app_mcdh47a1d1jl_bullets.splice(i, 1);
            continue;
        }

        bullet.draw();

        for (let j = app_mcdh47a1d1jl_asteroids.length - 1; j >= 0; j--) {
            const asteroid = app_mcdh47a1d1jl_asteroids[j];
            if (app_mcdh47a1d1jl_dist(bullet, asteroid) < asteroid.size) {
                app_mcdh47a1d1jl_score += 10;
                app_mcdh47a1d1jl_scoreDisplay.textContent = app_mcdh47a1d1jl_score;
                app_mcdh47a1d1jl_bullets.splice(i, 1);
                app_mcdh47a1d1jl_asteroids.splice(j, 1);
                if (asteroid.size > 10) {
                    app_mcdh47a1d1jl_createAsteroids(2, asteroid.size / 2, asteroid.x, asteroid.y);
                }
                break;
            }
        }
    }

    if (app_mcdh47a1d1jl_asteroids.length === 0) {
        app_mcdh47a1d1jl_endGame();
    }
}

function app_mcdh47a1d1jl_endGame() {
    clearInterval(app_mcdh47a1d1jl_gameLoopId);
    app_mcdh47a1d1jl_gameOver = true;
    app_mcdh47a1d1jl_finalScoreDisplay.textContent = app_mcdh47a1d1jl_score;
    app_mcdh47a1d1jl_gameOverScreen.style.display = 'flex';
}

const app_mcdh47a1d1jl_keys = {};

document.addEventListener('keydown', (event) => {
    app_mcdh47a1d1jl_keys[event.key] = true;
    if (event.code === 'Space') {
        event.preventDefault();
        if (!app_mcdh47a1d1jl_gameOver && app_mcdh47a1d1jl_startScreen.style.display === 'none'){
            app_mcdh47a1d1jl_bullets.push(new app_mcdh47a1d1jl_Bullet(app_mcdh47a1d1jl_ship.x, app_mcdh47a1d1jl_ship.y, app_mcdh47a1d1jl_ship.rotation));
        }
    }
});

document.addEventListener('keyup', (event) => {
    app_mcdh47a1d1jl_keys[event.key] = false;
});

function app_mcdh47a1d1jl_updateShipMovement() {
    if (!app_mcdh47a1d1jl_ship || app_mcdh47a1d1jl_gameOver || app_mcdh47a1d1jl_startScreen.style.display === 'flex') return;

    if (app_mcdh47a1d1jl_keys['ArrowLeft']) {
        app_mcdh47a1d1jl_ship.rotate(-1);
    }
    if (app_mcdh47a1d1jl_keys['ArrowRight']) {
        app_mcdh47a1d1jl_ship.rotate(1);
    }
    if (app_mcdh47a1d1jl_keys['ArrowUp']) {
        app_mcdh47a1d1jl_ship.setThrust(true);
    } else {
        app_mcdh47a1d1jl_ship.setThrust(false);
    }
}

setInterval(app_mcdh47a1d1jl_updateShipMovement, 1000 / 60);

app_mcdh47a1d1jl_startButton.addEventListener('click', app_mcdh47a1d1jl_startGame);
app_mcdh47a1d1jl_restartButton.addEventListener('click', app_mcdh47a1d1jl_startGame);

window.addEventListener('resize', () => {
    app_mcdh47a1d1jl_canvas.width = window.innerWidth;
    app_mcdh47a1d1jl_canvas.height = window.innerHeight;
    if (!app_mcdh47a1d1jl_gameOver && app_mcdh47a1d1jl_startScreen.style.display === 'none') {
       app_mcdh47a1d1jl_ship.x = window.innerWidth / 2;
       app_mcdh47a1d1jl_ship.y = window.innerHeight / 2;
    }
});

app_mcdh47a1d1jl_resetGame();
